CREATE TABLE PARTICIPANTES ( ID_PARTICIPANTE INT NOT NULL AUTO_INCREMENT, NUMPER VARCHAR(13) DEFAULT NULL, MATRICULA VARCHAR(12) DEFAULT NULL, NIF VARCHAR(18) DEFAULT NULL, NOMBRE VARCHAR(100) DEFAULT NULL, APELLIDO1 VARCHAR(80) DEFAULT NULL, APELLIDO2 VARCHAR(80) DEFAULT NULL, EMAIL VARCHAR(255) DEFAULT NULL, PREFIJO_TELEFONO_MOVIL VARCHAR(5) DEFAULT NULL,TELEFONO_MOVIL VARCHAR(13) DEFAULT NULL, FECHA_INICIO TIMESTAMP DEFAULT NULL, FECHA_FINALIZACION TIMESTAMP DEFAULT NULL, IDIOMA VARCHAR(2) DEFAULT NULL, PRIMARY KEY (ID_PARTICIPANTE));