CREATE TABLE IF NOT EXISTS PARTICIPANTES ( ID_PARTICIPANTE INT NOT NULL, NUMPER VARCHAR(13) DEFAULT NULL, MATRICULA VARCHAR(12) DEFAULT NULL, NIF VARCHAR(18) DEFAULT NULL, NOMBRE VARCHAR(100) DEFAULT NULL, APELLIDO1 VARCHAR(80) DEFAULT NULL, APELLIDO2 VARCHAR(80) DEFAULT NULL, EMAIL VARCHAR(255) DEFAULT NULL, PREFIJO_TELEFONO_MOVIL VARCHAR(5) DEFAULT NULL, ELEFONO_MOVIL VARCHAR(13) DEFAULT NULL, FECHA_INICIO TIMESTAMP DEFAULT NULL, FECHA_FINALIZACION TIMESTAMP DEFAULT NULL, IDIOMA VARCHAR(2) DEFAULT NULL, FECHA_NACIMIENTO DATE DEFAULT NULL, PRIMARY KEY (ID_PARTICIPANTE));
CREATE TABLE IF NOT EXISTS ATRIBUTOS ( ID_ATRIBUTO INT NOT NULL AUTO_INCREMENT,ID_PARTICIPANTE INT, ORDEN INT, VALOR VARCHAR(255), FOREIGN KEY (ID_PARTICIPANTE) REFERENCES PARTICIPANTES(ID_PARTICIPANTE), PRIMARY KEY (ID_ATRIBUTO));